mapper.select:
select a.id as ass_id,a.order_number,a.order_id, a.codes,a.stu_id,a.stu_name,a.ass_time,a.status, u.city_name as city,u.head_pic as pic,u.identity_name as identity from (select id,order_number,order_id,codes, stu_id,stu_name,ass_time,status,create_time from t_yp_assessments where is_deleted=0 and tec_id= #{uid,jdbcType=INTEGER} and status= #{status,jdbcType=INTEGER}
and create_time >(select create_time from t_yp_assessments where is_deleted=0 and id=#{offset,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by create_time limit #{limit,jdbcType=INTEGER}) a, (select id,city_name,head_pic,identity_name from t_yp_user_stu where is_lock=0 and is_deleted=0) u where a.stu_id=u.id order by a.create_time

select a.id as ass_id,a.order_number,a.order_id, a.codes,a.stu_id,a.stu_name,a.ass_time,a.status, u.city_name as city,u.head_pic as pic,u.identity_name as identity from (select id,order_number,order_id,codes, stu_id,stu_name,ass_time,status from t_yp_assessments where is_deleted=0 and tec_id= #{uid,jdbcType=INTEGER} and status= #{status,jdbcType=INTEGER}
and ass_time <(select ass_time from t_yp_assessments where is_deleted=0 and id=#{offset,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by ass_time desc limit #{limit,jdbcType=INTEGER}) a, (select id,city_name,head_pic,identity_name from t_yp_user_stu where is_lock=0 and is_deleted=0) u where a.stu_id=u.id order by a.ass_time desc

select count(id) as num
from t_yp_assessments
where tec_id = #{uid,jdbcType=INTEGER}
and status=#{status,jdbcType=INTEGER}
and is_deleted=0

select count(id) as num
from t_yp_assessments
where order_id = #{order_id,jdbcType=INTEGER}
and order_number = #{order_number,jdbcType=VARCHAR}
and status=#{ass_status,jdbcType=INTEGER}
and is_deleted=0
