mapper.select:
select id,father_id,father_name,name,level from t_yp_city where level=2 and is_deleted=0 and is_valid=0
and father_name=#{fatherName,jdbcType=VARCHAR}
order by order_code

from (select sort_word from t_yp_city where level=2 and is_deleted=0 and is_valid=0 group by sort_word) s, (select id,father_id,name,level,sort_word from t_yp_city where level=2 and is_deleted=0 and is_valid=0
and father_name=#{province,jdbcType=VARCHAR}
order by order_code) c where s.sort_word=c.sort_word order by s.sort_word
