choose.when:
select u.id as uid,u.name,u.user_code, f.id as fav_id,f.host as b_fav_id,f.host_type as fav_type from (select id,name,user_code from t_yp_user_stu where is_lock=0 and is_deleted=0 and id=#{uid,jdbcType=INTEGER}) u left join (select id,host,host_type,visitor,create_time from t_yp_favorites where visitor_type='stu' and is_deleted=0 and visitor=#{uid,jdbcType=INTEGER}
and create_time >= (select create_time from t_yp_favorites where visitor_type='stu' and is_deleted=0 and id=#{offset,jdbcType=INTEGER} and visitor=#{uid,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by create_time limit #{limit,jdbcType=INTEGER}) f on f.visitor=u.id

select u.id as uid,u.name,u.user_code, f.id as fav_id,f.host as b_fav_id,f.host_type as fav_type from (select id,name,user_code from t_yp_user_tech where is_lock=0 and is_deleted=0 and id=#{uid,jdbcType=INTEGER}) u left join (select id,host,host_type,visitor,create_time from t_yp_favorites where visitor_type='tec' and is_deleted=0 and visitor=#{uid,jdbcType=INTEGER}
and create_time >= (select create_time from t_yp_favorites where visitor_type='tec' and is_deleted=0 and id=#{offset,jdbcType=INTEGER} and visitor=#{uid,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by create_time limit #{limit,jdbcType=INTEGER}) f on f.visitor=u.id order by f.create_time

choose.otherwise:
select u.id as uid,u.name,'' as user_code, f.id as fav_id,f.host as b_fav_id,f.host_type as fav_type from (select id,name,user_code from t_yp_org where is_lock=0 and is_deleted=0 and id=#{uid,jdbcType=INTEGER}) u left join (select id,host,host_type,visitor,create_time from t_yp_favorites where visitor_type='org' and is_deleted=0 and visitor=#{uid,jdbcType=INTEGER}
and create_time >= (select create_time from t_yp_favorites where visitor_type='org' and is_deleted=0 and id=#{offset,jdbcType=INTEGER} and visitor=#{uid,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by create_time limit #{limit,jdbcType=INTEGER}) f on f.visitor=u.id order by f.create_time
