mapper.update:
update t_yp_group
set people_num=people_num+1
where id=#{id,jdbcType=INTEGER}

update t_yp_group
set people_num=people_num-1
where id=#{id,jdbcType=INTEGER}

mapper.select:
select id,name,pic,introduce,grade,people_num,order_code from t_yp_group where verify_status=1 and is_deleted=0
and order_code >(select order_code from t_yp_group where id=#{offset,jdbcType=INTEGER})
order by order_code, create_time desc limit #{limit,jdbcType=INTEGER}

select id,name,pic,introduce,grade,people_num,verify_status
from v_yp_listmy_group
where user_id=#{uid,jdbcType=INTEGER} 
and user_type=#{utype,jdbcType=VARCHAR}

	and order_code >(select order_code from v_yp_listmy_group 
	where id=#{offset,jdbcType=INTEGER}
	and user_id=#{uid,jdbcType=INTEGER} 
	and user_type=#{utype,jdbcType=VARCHAR})

order by order_code, create_time desc
limit #{limit,jdbcType=INTEGER}
