mapper.select:
select 

from t_yp_live_member
where is_deleted=0
and foreign_key=#{room_id,jdbcType=INTEGER}
and type='member'
and host=#{uid,jdbcType=INTEGER}
and host_type=#{utype,jdbcType=VARCHAR}

choose.when:
    		select d.host as uid,d.host_type as utype,d.type as member_type,
		    d.id,t.head_pic,t.name
		    from (select host,host_type,type,id
		    from t_yp_live_member
		    where foreign_key = #{room_id,jdbcType=INTEGER}
		    and type='owner'
		    and owner=#{uid,jdbcType=INTEGER}
		    and owner_type='tec'
		    and is_deleted=0) d,
		    (select id,name,head_pic
		    from t_yp_user_tech
		    where is_deleted=0 and is_lock=0
		    and id=#{uid,jdbcType=INTEGER}
		    
    			and name= #{name,jdbcType=VARCHAR}
   			) t
		    where d.host=t.id

    		select d.host as uid,d.host_type as utype,d.type as member_type,
		    d.id,u.head_pic,u.name
		    from (select host,host_type,type,id
		    from t_yp_live_member
		    where foreign_key = #{room_id,jdbcType=INTEGER}
		    and type='owner'
		    and owner=#{uid,jdbcType=INTEGER}
		    and owner_type='stu'
		    and is_deleted=0) d,
		    (select id,name,head_pic
		    from t_yp_user_stu
		    where is_deleted=0 and is_lock=0
		    and id=#{uid,jdbcType=INTEGER}
		    
    			and name= #{name,jdbcType=VARCHAR}
   			) u
		    where d.host=u.id
