mapper.update:
update t_yp_live_room
set pre_number = pre_number+#{preNumber,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select r.id,r.owner,r.owner_type,r.live_name,r.thumbnail, r.browse_number,r.chapter_number,r.pre_time,u.name, 0 as live_status,'' as chapter_name from (select id,owner,owner_type,live_name,thumbnail, browse_number,chapter_number,pre_time from t_yp_live_room where is_deleted=0 and is_publish=2 and pre_number>0 and chapter_number >0
and pre_time >= (select pre_time from t_yp_live_room where id=#{offset,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
and major_one=#{major_one,jdbcType=INTEGER}
and major_two=#{major_two,jdbcType=INTEGER}
and live_type=#{live_type,jdbcType=INTEGER}
order by pre_time asc limit #{limit,jdbcType=INTEGER}) r, (select id,name from t_yp_user_tech where is_deleted=0 and is_lock=0) u where r.owner=u.id order by r.pre_time asc

select r.id,r.owner,r.owner_type,r.live_name,r.thumbnail, r.browse_number,r.chapter_number,r.pre_time,u.name, 2 as live_status,'' as chapter_name from (select id,owner,owner_type,live_name,thumbnail, browse_number,chapter_number,pre_time,create_time from t_yp_live_room where is_deleted=0 and is_publish=2 and pre_number=0 and chapter_number >0
and pre_time <= (select pre_time from t_yp_live_room where id=#{offset,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
and major_one=#{major_one,jdbcType=INTEGER}
and major_two=#{major_two,jdbcType=INTEGER}
and live_type=#{live_type,jdbcType=INTEGER}
order by pre_time desc limit #{limit,jdbcType=INTEGER}) r, (select id,name from t_yp_user_tech where is_deleted=0 and is_lock=0) u where r.owner=u.id order by r.pre_time desc

select 

from t_yp_live_room
where is_deleted=0 and is_publish=2
and owner = #{uid,jdbcType=INTEGER}
and owner_type=#{utype,jdbcType=VARCHAR}
