mapper.select:
select o.id,o.order_number,o.chapter_id, o.chapter_name,o.money,o.pay_time, l.live_status,l.start_time,l.room_id,l.sd_url from (select id,order_number,chapter_id, chapter_name,money,pay_time from t_yp_order_course_detail where is_deleted=0 and type=11 and status=1 and is_pay=1 and stu_id= #{uid,jdbcType=INTEGER}
and pay_time <= (select pay_time from t_yp_order_course_detail where id=#{offset,jdbcType=INTEGER})
order by pay_time desc limit #{limit,jdbcType=INTEGER}) o, (select id,live_status,start_time,room_id,sd_url from t_yp_live_chapter_plan where is_deleted=0 and is_publish=2) l where o.chapter_id=l.id order by o.pay_time desc

select 

from t_yp_order_course_detail
where is_deleted=0 and type=11 
and status=1 and is_pay=1

	and stu_id= #{uid,jdbcType=INTEGER}


	and chapter_id=#{chapter_id,jdbcType=INTEGER}
