mapper.select:
select name
from t_yp_user_stu
where id=#{id,jdbcType=INTEGER}

select u.id as uid,'stu' as user_type,b.create_time,u.head_pic, u.name,u.identity_name,u.city_name,b.id as b_like_id from (select id,visitor,visitor_type,create_time from t_yp_bbs_like where foreign_key = #{fid,jdbcType=INTEGER} and is_deleted=0
and id > #{offset,jdbcType=INTEGER}
order by create_time limit #{limit,jdbcType=INTEGER}) b, (select id, head_pic, name, city_name,identity_name from t_yp_user_stu where 1=1 ) u where b.visitor=u.id order by b.create_time

