mapper.update:
update t_yp_works
set comment_num = comment_num+1
where id = #{id,jdbcType=INTEGER}

update t_yp_works
set like_num = like_num+1
where id = #{id,jdbcType=INTEGER}

mapper.select:
select w.id as stus_id,'work' as stus_type,w.comment_num, w.owner_type,w.like_num,w.browse_num,w.content, w.title,w.tag,w.create_time,w.remarks, s.identity_name as identity, s.id as owner,s.name as owner_name,s.city_name as city, s.head_pic as owner_head_pic from (select id,comment_num,like_num,browse_num,content,title, tag,create_time,owner,owner_type,remarks,order_code from t_yp_works where is_deleted=0 and owner_type='stu' and owner=#{uid,jdbcType=INTEGER}
and order_code <= (select order_code from t_yp_works where is_deleted=0 and id=#{offset,jdbcType=INTEGER}) and id <> #{offset,jdbcType=INTEGER}
order by order_code desc limit #{limit,jdbcType=INTEGER}) w, (select identity_name,city_name,head_pic,name,id from t_yp_user_stu where and id=#{uid,jdbcType=INTEGER}) s where w.owner = s.id order by w.order_code desc
