mapper.select:
select

from fens_transaction

	is_delete=0 
	
		and fens_user_id = #{fensUserId,jdbcType=INTEGER}
	
	
		and  trader_type = #{traderType,jdbcType=INTEGER}
	
	
		and trader_id = #{traderId,jdbcType=INTEGER}
	
	
		and trader_state = #{traderState,jdbcType=INTEGER}
	
	 order by entrust_price desc,create_date asc

select sum(trader_count) from fens_transaction
is_delete=0 and ((trader_type=1 and fens_user_id=#{uid,jdbcType=INTEGER} and trader_state in(0,1,2,4) ) or (trader_type=2 and trader_id=#{uid,jdbcType=INTEGER} and trader_state in(0,1,2,4)))
and id != #{id,jdbcType=INTEGER}
